            //access the results here....

          });
      }

      render() {
        const { id, name, salary, role } = this.state;
        return (
        <div className="App">
          <form onSubmit={this.onSubmit}>
          <label>
            Id:
            <input
              type="text"
              name="id"
              value={id}
              onChange={this.onChange}
            />
             </label>
             <label>
             Name:
            <input
              type="text"
              name="name"
              value={name}
              onChange={this.onChange}
            />
            </label>
            <label>
            Salary:
            <input
              type="text"
              name="salary"
              value={salary}
              onChange={this.onChange}
            />
            </label>
            <label>
            Role:
            <input
              name="role"
              type="text"
              value={role}
              onChange={this.onChange}
            />
            </label>
            <button type="submit">Submit</button>
          </form>
          </div>
        );
      }
    }
